      'import { BrowserRouter as Router, Routes, Route } from "react-router-dom";\nimport DashboardLayout from "@/polymet/layouts/dashboard-layout";\nimport ApiDashboard from "@/polymet/pages/api-dashboard";\nimport ApiCallsPage from "@/polymet/pages/api-calls";\nimport ApiCallDetailPage from "@/polymet/pages/api-call-detail";\n\nexport default function ApiDashboardPrototype() {\n  return (\n    <Router>\n      <Routes>\n        <Route\n          path="/"\n          element={\n            <DashboardLayout>\n              <ApiDashboard />\n            </DashboardLayout>\n          }\n        />\n\n        <Route\n          path="/api-calls"\n          element={\n            <DashboardLayout>\n              <ApiCallsPage />\n            </DashboardLayout>\n          }\n        />\n\n        <Route\n          path="/api-calls/:id"\n          element={\n            <DashboardLayout>\n              <ApiCallDetailPage />\n            </DashboardLayout>\n          }\n        />\n      </Routes>\n    </Router>\n  );\n}\n',
