      'import { Button } from "@/components/ui/button";\nimport { Input } from "@/components/ui/input";\nimport {\n  Select,\n  SelectContent,\n  SelectItem,\n  SelectTrigger,\n  SelectValue,\n} from "@/components/ui/select";\nimport ApiCallsTable from "@/polymet/components/api-calls-table";\nimport { API_CALLS } from "@/polymet/data/api-calls-data";\nimport { FilterIcon, RefreshCwIcon } from "lucide-react";\nimport { useState } from "react";\n\nexport default function ApiCallsPage() {\n  const [isRefreshing, setIsRefreshing] = useState(false);\n\n  const handleRefresh = () => {\n    setIsRefreshing(true);\n\n    // Simulate refresh delay\n    setTimeout(() => {\n      setIsRefreshing(false);\n    }, 1000);\n  };\n\n  return (\n    <div className="space-y-6">\n      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">\n        <h1 className="text-3xl font-bold">API Calls</h1>\n\n        <div className="flex items-center space-x-2">\n          <Button\n            variant="outline"\n            size="sm"\n            onClick={handleRefresh}\n            disabled={isRefreshing}\n          >\n            <RefreshCwIcon\n              className={`h-4 w-4 mr-2 ${isRefreshing ? "animate-spin" : ""}`}\n            />\n            Refresh\n          </Button>\n\n          <Select defaultValue="24h">\n            <SelectTrigger className="w-[130px]">\n              <SelectValue placeholder="Time Range" />\n            </SelectTrigger>\n            <SelectContent>\n              <SelectItem value="1h">Last hour</SelectItem>\n              <SelectItem value="24h">Last 24 hours</SelectItem>\n              <SelectItem value="7d">Last 7 days</SelectItem>\n              <SelectItem value="30d">Last 30 days</SelectItem>\n            </SelectContent>\n          </Select>\n        </div>\n      </div>\n\n      <ApiCallsTable calls={API_CALLS} />\n    </div>\n  );\n}\n',
